(1121, 63, 'profiles_1_job_title', ' Partner, Financial Services and Consumer Industries and CEO'),
(1122, 63, '_profiles_1_job_title', 'field_568e88bfe1a69'),
(1123, 63, 'profiles_1_biography', '<p class="name">Geoffrey is one of the most experienced consultants in financial PR and investor relations.  With over 28 years of experience he has advised numerous clients on maximising their valuations and corporate reputations across a wide range of areas, including flotations, mergers and acquisitions as well as on an on-going financial calendar basis.  Client experience includes advising Thomas Cook in their transformation and £1.6 billion capital refinancing, Zurich Insurance Group, Northern Rock, Aegon, Allied Irish Banks, Friends Provident and Fortis as well as many others.</p>\r\n\r\n<div class="biography">\r\n\r\nHe was previously Global President of FTI Consulting’s Strategic Communications division, formerly known as Financial Dynamics, which he joined in 2000.  Before that he was Head of Investor Relations at NatWest, where he led the bank’s defence against The Royal Bank of Scotland’s and the Bank of Scotland’s hostile bids, having joined the bank in 1990.  He began his career as a buy side investment analyst with Leopold Joseph in 1985.\r\n\r\nEmail: <a href="mailto:geoffrey.pelham-lane@camarco.co.uk">geoffrey.pelham-lane@camarco.co.uk</a>\r\n\r\nDirect no: 020 3757 4985\r\n\r\n</div>'),
(1124, 63, '_profiles_1_biography', 'field_568e8900e1a6b'),
